var http=require("http"),assert=require("assert"),methods=require("..");describe("methods",function(){http.METHODS?it("is a lowercased http.METHODS",function(){var e=http.METHODS.map(function(e){return e.toLowerCase()});assert.deepEqual(e,methods)}):(it("contains GET, POST, PUT, and DELETE",function(){assert.notEqual(methods.indexOf("get"),-1),assert.notEqual(methods.indexOf("post"),-1),assert.notEqual(methods.indexOf("put"),-1),assert.notEqual(methods.indexOf("delete"),-1)}),it("is all lowercase",function(){for(var e=0;e<methods.length;e++)assert(methods[e],methods[e].toLowerCase(),methods[e]+" isn't all lowercase")}))});